import { GOOGLE_SIGN_IN_SUCCESS, EMAIL_SIGN_IN_SUCCESS, EMAIL_SIGN_IN_FAILURE, GOOGLE_SIGN_IN_FAILURE } from "./user.action"

const INITIAL_STATE = {
  currentUser: null
}


export default function userReducer(state = INITIAL_STATE, action) {
  switch(action.type) {
    case GOOGLE_SIGN_IN_SUCCESS:
    case EMAIL_SIGN_IN_SUCCESS: 
      return {
        ...state,
        currentUser: action.user,
        error: null
      }
    case GOOGLE_SIGN_IN_FAILURE:
    case EMAIL_SIGN_IN_FAILURE:
      return {
        ...state,
        error: action.error
      }
    default:
      return state
  }
}
