import { GOOGLE_SIGN_IN_SUCCESS, EMAIL_SIGN_IN_SUCCESS } from "./user.action"

const INITIAL_STATE = {
  currentUser: null
}


export default function userReducer(state = INITIAL_STATE, action) {
  switch(action.type) {
    case GOOGLE_SIGN_IN_SUCCESS:
    case EMAIL_SIGN_IN_SUCCESS : 
      return {
        ...state,
        currentUser: action.user
      }
    default:
      return state
  }
}
